import{_ as p,r as s,o,c as n,a as e,d as t,F as d,e as i,b as r}from"./app.873f0dc8.js";const h={},c=i('<h1 id="\u72B6\u6001\u7BA1\u7406" tabindex="-1"><a class="header-anchor" href="#\u72B6\u6001\u7BA1\u7406" aria-hidden="true">#</a> \u72B6\u6001\u7BA1\u7406</h1><h3 id="\u4EC0\u4E48\u662F\u72B6\u6001" tabindex="-1"><a class="header-anchor" href="#\u4EC0\u4E48\u662F\u72B6\u6001" aria-hidden="true">#</a> \u4EC0\u4E48\u662F\u72B6\u6001</h3><p>\u72B6\u6001\u662F\u6570\u636E\u7684\u53D8\u5316</p><p><strong>\u901A\u8FC7\u89C6\u56FE\u4EA4\u4E92\u6216\u8005\u5176\u4ED6\u65B9\u5F0F\u89E6\u53D1\u72B6\u6001\u7684\u53D8\u5316\uFF0C\u72B6\u6001\u53D8\u5316\u8054\u52A8\u89C6\u56FE\u7684\u6E32\u67D3\u548C\u903B\u8F91\u7684\u6267\u884C\uFF0C\u8FD9\u5C31\u662F\u524D\u7AEF\u5E94\u7528\u7684\u6838\u5FC3\u3002</strong></p><p>\u4E3A\u4EC0\u4E48\u4E4B\u524D jQuery \u65F6\u4EE3\u6CA1\u548B\u542C\u8BF4\u72B6\u6001\u7BA1\u7406\u7684\u6982\u5FF5\uFF0C\u800C Vue\u3001React \u65F6\u4EE3\u7ECF\u5E38\u542C\u5230\u5462\uFF1F</p><p>jQuery \u65F6\u4EE3\u662F\u624B\u52A8\u628A\u6570\u636E\u6E32\u67D3\u5230\u89C6\u56FE\u548C\u6267\u884C\u6570\u636E\u53D8\u5316\u4E4B\u540E\u7684\u903B\u8F91\u7684\uFF0C\u5B83\u53EF\u80FD\u6CA1\u6709\u660E\u786E\u7684\u72B6\u6001\u8FD9\u4E00\u5C42\uFF0C\u800C\u662F\u76F4\u63A5\u628A\u6570\u636E\u6E32\u67D3\u6210 dom\uFF0C\u4E0B\u6B21\u9700\u8981\u6570\u636E\u4E5F\u662F\u4ECE dom \u6765\u53D6\u7684\u3002</p><p>\u800C Vue\u3001React \u524D\u7AEF\u6846\u67B6\u7684\u65F6\u4EE3\u4E0D\u9700\u8981\u624B\u52A8\u64CD\u4F5C dom \u548C\u6267\u884C\u6570\u636E\u53D8\u5316\u4E4B\u540E\u7684\u903B\u8F91\uFF0C\u53EA\u8981\u7BA1\u7406\u597D\u72B6\u6001\uFF0C\u7531\u524D\u7AEF\u6846\u67B6\u8D1F\u8D23\u72B6\u6001\u53D8\u5316\u4E4B\u540E\u7684\u5904\u7406\u3002</p><p>\u72B6\u6001\u7BA1\u7406\u7BA1\u7406\u7684\u662F\u4EC0\u4E48\u5462\uFF1F</p><h3 id="\u4EC0\u4E48\u662F\u72B6\u6001\u7BA1\u7406" tabindex="-1"><a class="header-anchor" href="#\u4EC0\u4E48\u662F\u72B6\u6001\u7BA1\u7406" aria-hidden="true">#</a> \u4EC0\u4E48\u662F\u72B6\u6001\u7BA1\u7406</h3><p>\u72B6\u6001\u7BA1\u7406\u5177\u4F53\u6709\u4E24\u5C42\u542B\u4E49\uFF1A</p><ul><li>\u72B6\u6001\u53D8\u5316\u4E4B\u524D\u7684\u903B\u8F91\uFF0C\u4E00\u822C\u662F\u5F02\u6B65\u7684</li><li>\u72B6\u6001\u53D8\u5316\u4E4B\u540E\u7684\u8054\u52A8\u5904\u7406\uFF0C\u6BD4\u5982\u6E32\u67D3\u89C6\u56FE\u6216\u6267\u884C\u67D0\u6BB5\u903B\u8F91</li></ul><p>\u6BD4\u5982 React \u7684 setState \u4E0D\u4F1A\u9A6C\u4E0A\u4FEE\u6539\u72B6\u6001\uFF0C\u800C\u662F\u5F02\u6B65\u7684\u6279\u91CF\u7684\u6267\u884C\uFF0C\u628A\u72B6\u6001\u505A\u4E00\u4E0B\u5408\u5E76\u3002</p><p>\u6BD4\u5982 Redux \u7684 action \u5728\u4FEE\u6539\u5168\u5C40 state \u4E4B\u524D\u4E5F\u662F\u8981\u7ECF\u5386\u4E2D\u95F4\u4EF6\u7684\u5904\u7406\u7684\u3002</p><p>\u8FD9\u4E9B\u90FD\u662F\u72B6\u6001\u53D8\u5316\u4E4B\u524D\u7684\u5F02\u6B65\u8FC7\u7A0B\u7684\u7BA1\u7406\uFF0C\u662F\u72B6\u6001\u7BA1\u7406\u7684\u7B2C\u4E00\u5C42\u542B\u4E49</p><p>\u518D\u6BD4\u5982 React setState \u4FEE\u6539\u4E86\u72B6\u6001\u4E4B\u540E\u8981\u89E6\u53D1\u89C6\u56FE\u7684\u6E32\u67D3\u548C\u751F\u547D\u5468\u671F\u51FD\u6570\u7684\u6267\u884C\uFF0Chooks \u5728\u4F9D\u8D56\u6570\u7EC4\u7684\u72B6\u6001\u53D8\u5316\u4E4B\u540E\u4E5F\u4F1A\u91CD\u65B0\u6267\u884C\u3002\uFF08vue \u7684 data \u4FEE\u6539\u4E4B\u540E\u4F1A\u91CD\u65B0\u6E32\u67D3\u89C6\u56FE\u3001\u6267\u884C computed \u548C watch \u903B\u8F91\uFF09</p><p>Redux \u4FEE\u6539\u4E86\u5168\u5C40\u72B6\u6001\u4E4B\u540E\u8981\u901A\u77E5\u7EC4\u4EF6\u505A\u6E32\u67D3\u6216\u8005\u505A\u5176\u4ED6\u903B\u8F91\u7684\u5904\u7406\uFF0CVuex\u3001Mobx \u7B49\u90FD\u662F\u3002</p><p>\u8FD9\u4E9B\u662F\u72B6\u6001\u53D8\u5316\u4E4B\u540E\u7684\u8054\u52A8\u5904\u7406\u7684\u7BA1\u7406\uFF0C\u662F\u72B6\u6001\u7BA1\u7406\u7684\u7B2C\u4E8C\u5C42\u542B\u4E49</p><p>\u6211\u4EEC\u77E5\u9053\u4E86\u4EC0\u4E48\u662F\u72B6\u6001\uFF0C\u4EC0\u4E48\u662F\u72B6\u6001\u7BA1\u7406\uFF0C\u90A3\u524D\u7AEF\u6846\u67B6 Vue\u3001React \u548C\u5168\u5C40\u72B6\u6001\u7BA1\u7406\u7684\u5E93 Redux\u3001Mobx\u3001Vuex \u90FD\u662F\u600E\u4E48\u5B9E\u73B0\u72B6\u6001\u7BA1\u7406\u7684\u5462</p><h3 id="\u72B6\u6001\u7BA1\u7406\u7684\u4E24\u79CD\u5B9E\u73B0\u601D\u8DEF" tabindex="-1"><a class="header-anchor" href="#\u72B6\u6001\u7BA1\u7406\u7684\u4E24\u79CD\u5B9E\u73B0\u601D\u8DEF" aria-hidden="true">#</a> \u72B6\u6001\u7BA1\u7406\u7684\u4E24\u79CD\u5B9E\u73B0\u601D\u8DEF</h3><p>\u72B6\u6001\u4E0D\u4F1A\u662F\u4E00\u4E2A\uFF0C\u591A\u4E2A\u72B6\u6001\u7684\u96C6\u5408\u4F1A\u7528\u5BF9\u8C61\u7684 key\u3001value \u6765\u8868\u793A\uFF0C\u6BD4\u5982 React \u7684 state \u5BF9\u8C61\uFF0CVue \u7684 data \u5BF9\u8C61\uFF08\u867D\u7136\u53EB data \u4E5F\u662F\u6307\u7684\u72B6\u6001\uFF09\u3002</p><p>\u600E\u4E48\u76D1\u542C\u4E00\u4E2A\u5BF9\u8C61\u7684\u53D8\u5316\u5462\uFF1F</p><p>\u6211\u4EEC\u662F\u4E0D\u662F\u53EF\u4EE5\u63D0\u4F9B\u4E00\u4E2A api \u6765\u4FEE\u6539\uFF0C\u5728\u8FD9\u4E2A api \u5185\u505A state \u53D8\u5316\u4E4B\u524D\u7684\u5904\u7406\uFF0C\u5E76\u4E14\u5728 state \u53D8\u5316\u4E4B\u540E\u505A\u8054\u52A8\u5904\u7406\u3002</p><p>\u8FD9\u6837\u7684\u65B9\u6848\u53EA\u80FD\u901A\u8FC7 api \u89E6\u53D1\u72B6\u6001\u4FEE\u6539\uFF0C\u76F4\u63A5\u4FEE\u6539 state \u662F\u89E6\u53D1\u4E0D\u4E86\u72B6\u6001\u7BA1\u7406\u903B\u8F91\u7684\u3002</p><p>React \u7684 setState \u5C31\u662F\u8FD9\u79CD\u601D\u8DEF\uFF0C\u901A\u8FC7 setState \u4FEE\u6539\u72B6\u6001\u4F1A\u505A\u72B6\u6001\u53D8\u5316\u4E4B\u524D\u7684\u6279\u91CF\u5F02\u6B65\u7684\u72B6\u6001\u5408\u5E76\uFF0C\u4F1A\u89E6\u53D1\u72B6\u6001\u53D8\u5316\u4E4B\u540E\u89C6\u56FE\u6E32\u67D3\u548C hooks\u3001\u751F\u547D\u5468\u671F\u7684\u91CD\u65B0\u6267\u884C\u3002\u4F46\u662F\u76F4\u63A5\u4FEE\u6539 state \u662F\u6CA1\u7528\u7684\u3002</p><p>\u90A3\u600E\u4E48\u8BA9\u76F4\u63A5\u4FEE\u6539\u72B6\u6001\u4E5F\u80FD\u76D1\u542C\u5230\u53D8\u5316\u5462\uFF1F</p><p>\u53EF\u4EE5\u5BF9\u72B6\u6001\u5BF9\u8C61\u505A\u4E00\u5C42\u4EE3\u7406\uFF0C\u4EE3\u7406\u5B83\u7684 get\u3001set\uFF0C\u5F53\u6267\u884C\u72B6\u6001\u7684 get \u7684\u65F6\u5019\u628A\u4F9D\u8D56\u8BE5\u72B6\u6001\u7684\u903B\u8F91\u6536\u96C6\u8D77\u6765\uFF0C\u5F53 set \u4FEE\u6539\u72B6\u6001\u7684\u65F6\u5019\u901A\u77E5\u6240\u6709\u4F9D\u8D56\u5B83\u7684\u903B\u8F91\uFF08\u89C6\u56FE\u6E32\u67D3\u3001\u903B\u8F91\u6267\u884C\uFF09\u505A\u66F4\u65B0\u3002</p><p>Vue \u7684 data \u76D1\u542C\u53D8\u5316\u5C31\u662F\u7528\u7684\u8FD9\u79CD\u601D\u8DEF\uFF0C\u5728\u72B6\u6001 get \u7684\u65F6\u5019\u628A\u4F9D\u8D56\u5C01\u88C5\u6210 Watcher\uFF0C\u5F53 set \u7684\u65F6\u5019\u901A\u77E5\u6240\u6709 Watcher \u505A\u66F4\u65B0\u3002</p><p>\u8FD9\u79CD\u601D\u8DEF\u53EB\u505A\u54CD\u5E94\u5F0F\uFF08reactive\uFF09\uFF0C\u4E5F\u5C31\u662F\u72B6\u6001\u53D8\u5316\u4E4B\u540E\u81EA\u52A8\u54CD\u5E94\u53D8\u5316\u505A\u8054\u52A8\u5904\u7406\u7684\u610F\u601D\u3002</p><p>\u4EE3\u7406 get\u3001set \u53EF\u4EE5\u7528 Object.defineProperty \u7684 api\uFF0C\u4F46\u662F\u5B83\u4E0D\u80FD\u76D1\u542C\u52A8\u6001\u589E\u5220\u7684\u5BF9\u8C61\u5C5E\u6027\uFF0C\u6240\u4EE5 Vue3 \u6539\u4E3A\u4E86\u7528 Proxy \u7684 api \u5B9E\u73B0\u3002</p><p>\u76D1\u542C\u5BF9\u8C61\u7684\u53D8\u5316\u5C31\u8FD9\u4E24\u79CD\u65B9\u5F0F\uFF1A</p><ul><li>\u63D0\u4F9B api \u6765\u4FEE\u6539\uFF0C\u5185\u90E8\u505A\u8054\u52A8\u5904\u7406\u3002</li><li>\u5BF9\u5BF9\u8C61\u505A\u4E00\u5C42\u4EE3\u7406\uFF0Cset \u7684\u65F6\u5019\u505A\u8054\u52A8\u5904\u7406\uFF0C\u901A\u77E5 get \u65F6\u6536\u96C6\u7684\u6240\u6709\u4F9D\u8D56\u3002</li></ul><h4 id="\u524D\u7AEF\u6846\u67B6\u72B6\u6001\u53D8\u5316\u7684\u6027\u80FD\u4F18\u5316" tabindex="-1"><a class="header-anchor" href="#\u524D\u7AEF\u6846\u67B6\u72B6\u6001\u53D8\u5316\u7684\u6027\u80FD\u4F18\u5316" aria-hidden="true">#</a> \u524D\u7AEF\u6846\u67B6\u72B6\u6001\u53D8\u5316\u7684\u6027\u80FD\u4F18\u5316</h4><p>\u4F46\u662F\u9891\u7E41\u7684\u4FEE\u6539 state \u4E0D\u662F\u6BCF\u6B21\u90FD\u8981\u505A\u8054\u52A8\u5904\u7406\uFF0C\u6709\u4E00\u4E9B\u53EF\u4EE5\u5408\u5E76\u7684\uFF0C\u6BD4\u5982\u4E24\u6B21\u90FD\u628A\u989C\u8272\u6539\u4E3A\u7EA2\u8272\uFF0C\u90A3\u540E\u7EED\u903B\u8F91\u5C31\u6CA1\u5FC5\u8981\u6267\u884C\u4E24\u6B21\uFF0C\u9700\u8981\u518D\u505A\u4E9B\u6027\u80FD\u65B9\u9762\u7684\u4F18\u5316\u3002</p><p>\u6240\u4EE5 React \u7684 setState \u662F\u5F02\u6B65\u7684\uFF0C\u4F1A\u505A\u6279\u91CF\u7684 state \u5408\u5E76\uFF08\u6CE8\u610F\uFF0CReact \u7684 setState \u4F20\u5165\u7684\u4E0D\u662F\u6700\u7EC8\u7684 state\uFF0C\u800C\u662F state \u7684 diff\uFF0CReact \u5185\u90E8\u53BB\u628A\u8FD9\u4E9B diff state \u66F4\u65B0\u5230 state\uFF09\u3002</p><p><img src="https://s2.loli.net/2022/05/10/FzBqehvl64YwXaR.png" alt="image-20220510082210663"></p><p>\u800C Vue \u56E0\u4E3A\u662F\u76F4\u63A5\u4FEE\u6539\u7684\u540C\u4E00\u4E2A\u5BF9\u8C61\uFF0C\u6240\u4EE5\u6CA1\u5FC5\u8981\u505A\u5565\u5408\u5E76\uFF0C\u5B83\u7684 Watcher \u6267\u884C\u662F\u5F02\u6B65\u7684\uFF0C\u5BF9\u591A\u6B21\u653E\u5230\u961F\u5217\u91CC\u7684 Watcher \u505A\u4E0B\u53BB\u91CD\u5C31\u884C\u4E86\u3002</p><p><img src="https://s2.loli.net/2022/05/10/5XvbD1KI2c7sUTi.png" alt="image-20220510082235275"></p><h3 id="\u7EC4\u4EF6\u95F4\u7684\u72B6\u6001\u7BA1\u7406" tabindex="-1"><a class="header-anchor" href="#\u7EC4\u4EF6\u95F4\u7684\u72B6\u6001\u7BA1\u7406" aria-hidden="true">#</a> \u7EC4\u4EF6\u95F4\u7684\u72B6\u6001\u7BA1\u7406</h3><p>\u7EC4\u4EF6\u5185\u7684\u72B6\u6001\u7BA1\u7406\u5C31\u662F\u8FD9\u6837\u7684\uFF0C\u5229\u7528\u524D\u7AEF\u6846\u67B6\u81EA\u5E26\u7684 state \u673A\u5236\u6765\u7BA1\u7406\u3002</p><p>\u90A3\u7EC4\u4EF6\u4E4B\u95F4\u5462\uFF1F\u4E00\u4E2A\u7EC4\u4EF6\u7684 state \u53D8\u4E86\u5982\u4F55\u8054\u52A8\u5176\u4ED6\u7EC4\u4EF6\u53D8\u5316\uFF1F</p><h4 id="props" tabindex="-1"><a class="header-anchor" href="#props" aria-hidden="true">#</a> props</h4><p>\u901A\u8FC7 props\uFF0C\u628A\u5F53\u524D\u7EC4\u4EF6\u7684 state \u4F5C\u4E3A props \u4F20\u5165\u5176\u4ED6\u7EC4\u4EF6\u5C31\u884C\u4E86\uFF0C\u8FD9\u6837\u5C31\u80FD\u8054\u52A8\u53D8\u5316\u3002</p><p>\u4F46\u662F props \u53EA\u80FD\u4E00\u5C42\u5C42\u4F20\u9012\uFF0C\u5982\u679C\u7EC4\u4EF6\u548C\u60F3\u8054\u52A8\u53D8\u5316\u7684\u7EC4\u4EF6\u76F8\u9694\u5F88\u591A\u5C42\uFF0C\u4F20\u9012 props \u5C31\u5F88\u9EBB\u70E6\u3002</p><p>\u8FD9\u79CD\u60C5\u51B5\u4E0B\u524D\u7AEF\u6846\u67B6\u4E5F\u90FD\u63D0\u4F9B\u4E86\u89E3\u51B3\u65B9\u6848\uFF0CReact \u63D0\u4F9B\u4E86 Context\u3001Vue \u63D0\u4F9B\u4E86 Event Bus\u3002</p><h4 id="context\u3001event-bus" tabindex="-1"><a class="header-anchor" href="#context\u3001event-bus" aria-hidden="true">#</a> Context\u3001Event Bus</h4><p>React \u7EC4\u4EF6\u53EF\u4EE5\u5728 context \u4E2D\u5B58\u653E state\uFF0C\u5F53 context \u4E2D\u7684 state \u53D8\u5316\u7684\u65F6\u5019\u4F1A\u76F4\u63A5\u89E6\u53D1\u5173\u8054\u7EC4\u4EF6\u7684\u6E32\u67D3\u3002</p><p>Vue \u53EF\u4EE5\u5728\u4E00\u4E2A\u7EC4\u4EF6\u5185 emit \u4E00\u4E2A\u4E8B\u4EF6\uFF0C\u7136\u540E\u53E6\u4E00\u4E2A\u7EC4\u4EF6 on \u8FD9\u4E2A\u4E8B\u4EF6\uFF0C\u7136\u540E\u66F4\u65B0\u81EA\u5DF1\u7684 data \u6765\u89E6\u53D1\u6E32\u67D3\u3002\u4E0D\u8FC7\u8FD9\u4E24\u4E2A api \u5728 Vue3 \u90FD\u5E9F\u5F03\u4E86\u3002</p><p>\u8FD9\u79CD\u524D\u7AEF\u6846\u67B6\u81EA\u5E26\u7684\u4EFB\u610F\u5C42\u7EC4\u4EF6\u7684\u72B6\u6001\u8054\u52A8\u65B9\u6848\u53EA\u80FD\u5904\u7406\u7B80\u5355\u7684\u573A\u666F\uFF0C\u590D\u6742\u7684\u573A\u666F\u8FD8\u662F\u5F97\u7528\u5168\u5C40\u72B6\u6001\u7BA1\u7406\u5E93\uFF0C\u6BD4\u5982 Redux\u3001Vuex\u3001Mobx \u8FD9\u4E9B\u3002</p><p>\u4E3A\u4EC0\u4E48\u8FD9\u4E48\u8BF4\u5462\uFF1F</p><p>\u8FD8\u8BB0\u5F97\u72B6\u6001\u7BA1\u7406\u7684\u4E24\u5C42\u542B\u4E49\u4E48\uFF1F\u72B6\u6001\u53D8\u5316\u524D\u7684\u5F02\u6B65\u8FC7\u7A0B\u7684\u7BA1\u7406\uFF0C\u72B6\u6001\u53D8\u5316\u540E\u7684\u8054\u52A8\u5904\u7406\u3002</p><p>Context \u548C Event Bus \u90FD\u53EA\u505A\u5230\u4E86\u72B6\u6001\u53D8\u5316\u540E\u7684\u8054\u52A8\u5904\u7406\uFF0C\u4F46\u662F\u6CA1\u6709\u5BF9\u72B6\u6001\u53D8\u5316\u524D\u7684\u5F02\u6B65\u8FC7\u7A0B\u7BA1\u7406\u505A\u652F\u6301\u3002</p><h4 id="redux\u3001mobx\u3001vuex" tabindex="-1"><a class="header-anchor" href="#redux\u3001mobx\u3001vuex" aria-hidden="true">#</a> Redux\u3001Mobx\u3001Vuex</h4><p>redux \u5C31\u63D0\u4F9B\u4E86\u4E2D\u95F4\u4EF6\u7684\u673A\u5236\uFF0C\u7EC4\u4EF6\u91CC\u53D1\u9001 action \u5230 store\uFF08\u5B58\u653E\u5168\u5C40 state \u7684\u5730\u65B9\uFF09\uFF0C\u4E4B\u524D\u4F1A\u7ECF\u5386\u5C42\u5C42\u4E2D\u95F4\u4EF6\u7684\u5904\u7406\uFF0C\u5728\u8FD9\u91CC\u5C31\u53EF\u4EE5\u505A\u4E00\u4E9B\u53EF\u590D\u7528\u7684\u903B\u8F91\u7684\u5C01\u88C5\uFF0C\u6BD4\u5982 loading \u7684\u5904\u7406\uFF0C\u4E5F\u53EF\u4EE5\u7ED3\u5408 rxjs \u8FD9\u79CD\u5F02\u6B65\u8FC7\u7A0B\u5904\u7406\u65B9\u6848\u3002</p><p>redux \u91CC\u6700\u5E38\u7528\u7684\u4E2D\u95F4\u4EF6\u5C31\u662F redux-saga \u548C redux-observable \u4E86\uFF0C\u8FD9\u4FE9\u90FD\u662F\u505A\u5F02\u6B65\u8FC7\u7A0B\u7684\u7BA1\u7406\u7684\u3002</p><p>redux-saga \u662F\u57FA\u4E8E generator \u5B9E\u73B0\u7684\uFF0C\u4E0D\u7BA1\u662F\u540C\u6B65\u8FD8\u662F\u5F02\u6B65\uFF0C\u90FD\u53EA\u8981\u58F0\u660E\u5F0F\u7684\u63CF\u8FF0\u8981\u6267\u884C\u7684\u903B\u8F91\u5C31\u884C\uFF0C\u7531 saga \u5185\u90E8\u7684\u6267\u884C\u5668\u4F1A\u53BB\u505A\u540C\u6B65\u6216\u5F02\u6B65\u7684\u5904\u7406\uFF0C\u63CF\u8FF0\u5F02\u6B65\u903B\u8F91\u5C31\u5F88\u7B80\u6D01\uFF0C\u800C\u4E14 redux-saga \u63D0\u4F9B\u4E86\u5F88\u591A\u5185\u7F6E\u7684\u903B\u8F91\u5C01\u88C5\u3002</p><p>\u548C React \u642D\u914D\u4F7F\u7528\u7684\u8BDD\uFF0C\u9700\u8981\u628A\u7EC4\u4EF6\u6DFB\u52A0\u5230\u72B6\u6001\u7684\u4F9D\u8D56\u4E2D\uFF0C\u8FD9\u4E2A\u4E0D\u7528\u81EA\u5DF1\u8C03\u7528 subscribe \u4E4B\u7C7B\u7684 api\uFF0C\u76F4\u63A5\u7528\u4E00\u4E9B\u5C01\u88C5\u597D\u7684\u9AD8\u9636\u7EC4\u4EF6\uFF08\u63A5\u53D7\u7EC4\u4EF6\u4F5C\u4E3A\u53C2\u6570\u8FD4\u56DE\u65B0\u7684\u7EC4\u4EF6\u7684\u7EC4\u4EF6\uFF09\u5C31\u884C\uFF0C\u6BD4\u5982 react-redux \u7684 connect\uFF0Cmobx-react \u7684 observer</p><h3 id="\u603B\u7ED3" tabindex="-1"><a class="header-anchor" href="#\u603B\u7ED3" aria-hidden="true">#</a> \u603B\u7ED3</h3><p>\u4E0D\u7BA1\u662F\u524D\u7AEF\u6846\u67B6\u5185\u7F6E\u7684\u7EC4\u4EF6\u5185\u72B6\u6001\u53D8\u5316\u7BA1\u7406\u7684\u65B9\u6848\uFF08react \u7684 setState\u3001vue \u7684\u76F4\u63A5\u4FEE\u6539 data\uFF09\uFF0C\u8FD8\u662F\u524D\u7AEF\u6846\u67B6\u63D0\u4F9B\u7684\u7EC4\u4EF6\u95F4\u7684\u72B6\u6001\u7BA1\u7406\u65B9\u6848\uFF08props\u3001react \u7684 context\u3001vue \u7684 event bus\uFF09\uFF0C\u6216\u662F\u7B2C\u4E09\u65B9\u7684\u5168\u5C40\u72B6\u6001\u7BA1\u7406\u65B9\u6848\uFF08redux\u3001vuex\u3001mobx \u7B49\uFF09\uFF0C\u90FD\u6CA1\u6709\u8131\u79BB\u90A3\u4E24\u79CD\u5B9E\u73B0\u72B6\u6001\u7BA1\u7406\u7684\u65B9\u5F0F\uFF1A\u63D0\u4F9B\u4FEE\u6539\u72B6\u6001\u7684 api \u6216\u8005\u5BF9\u72B6\u6001\u5BF9\u8C61\u505A\u4E00\u5C42\u54CD\u5E94\u5F0F\u4EE3\u7406\u3002\u4E5F\u6CA1\u6709\u8131\u79BB\u72B6\u6001\u7BA1\u7406\u7684\u4E24\u5C42\u542B\u4E49\uFF1A\u5BF9\u72B6\u6001\u53D8\u5316\u524D\u7684\u5F02\u6B65\u8FC7\u7A0B\u505A\u7BA1\u7406\uFF0C\u72B6\u6001\u53D8\u5316\u540E\u505A\u8054\u52A8\u5904\u7406\u3002\u53EA\u4E0D\u8FC7\u5B83\u4EEC\u7528\u5728\u4E86\u4E0D\u540C\u7684\u5730\u65B9\uFF08\u524D\u7AEF\u6846\u67B6\u5185\u3001\u5168\u5C40\u72B6\u6001\u7BA1\u7406\u5E93\uFF09\uFF0C\u63D0\u4F9B\u4E86\u4E0D\u540C\u7684\u5C01\u88C5\u5F62\u5F0F\uFF08\u5BF9\u8C61\u3001\u51FD\u6570\uFF09\uFF0C\u57FA\u4E8E\u4E0D\u540C\u7684\u601D\u60F3\uFF08\u51FD\u6570\u5F0F\u3001\u9762\u5411\u5BF9\u8C61\uFF09\u7ED3\u5408\u4E86\u4E0D\u540C\u7684\u5F02\u6B65\u7BA1\u7406\u65B9\u6848\uFF08rxjs\u3001generator + \u81EA\u5B9A\u4E49\u6267\u884C\u5668\uFF09\u3002</p><p>\u6240\u4EE5\uFF0C<strong>\u72B6\u6001\u5C31\u662F\u6570\u636E\u7684\u53D8\u5316\u3002\u524D\u7AEF\u5E94\u7528\u7684\u6838\u5FC3\u95EE\u9898\u5C31\u662F\u7BA1\u7406\u72B6\u6001\uFF0C\u7BA1\u7406\u72B6\u6001\u53D8\u5316\u4E4B\u524D\u7684\u901A\u8FC7\u89C6\u56FE\u6216\u8005\u5176\u4ED6\u65B9\u5F0F\u89E6\u53D1\u7684\u5F02\u6B65\u8FC7\u7A0B\uFF0C\u7BA1\u7406\u72B6\u6001\u53D8\u5316\u4E4B\u540E\u7684\u8054\u52A8\u6E32\u67D3\u548C\u8054\u52A8\u7684\u903B\u8F91\u6267\u884C</strong></p><h2 id="\u53C2\u8003\u8D44\u6599" tabindex="-1"><a class="header-anchor" href="#\u53C2\u8003\u8D44\u6599" aria-hidden="true">#</a> \u53C2\u8003\u8D44\u6599</h2>',60),u={href:"https://zhuanlan.zhihu.com/p/53599723",target:"_blank",rel:"noopener noreferrer"},l=r("Vuex\u3001Flux\u3001Redux\u3001Redux-saga\u3001Dva\u3001MobX"),x={href:"https://github.com/ascoders/weekly/blob/v2/042.%E7%B2%BE%E8%AF%BB%E3%80%8A%E5%89%8D%E7%AB%AF%E6%95%B0%E6%8D%AE%E6%B5%81%E5%93%B2%E5%AD%A6%E3%80%8B.md",target:"_blank",rel:"noopener noreferrer"},b=r("\u7CBE\u8BFB\u300A\u524D\u7AEF\u6570\u636E\u6D41\u54F2\u5B66\u300B"),_={href:"https://mp.weixin.qq.com/s/oWuD-QwZuyEyt0fVWSHNeQ",target:"_blank",rel:"noopener noreferrer"},f=r("\u7406\u89E3\u4E86\u72B6\u6001\u7BA1\u7406\uFF0C\u5C31\u7406\u89E3\u4E86\u524D\u7AEF\u5F00\u53D1\u7684\u6838\u5FC3");function g(m,v){const a=s("ExternalLinkIcon");return o(),n(d,null,[c,e("ul",null,[e("li",null,[e("a",u,[l,t(a)])]),e("li",null,[e("a",x,[b,t(a)])]),e("li",null,[e("a",_,[f,t(a)])])])],64)}var E=p(h,[["render",g],["__file","\u72B6\u6001\u7BA1\u7406.html.vue"]]);export{E as default};
