import{_ as s,c as a,o as l,d as n}from"./app.590c1fbf.js";const m=JSON.parse('{"title":"常用命令","description":"","frontmatter":{},"headers":[{"level":2,"title":"常用命令列表","slug":"常用命令列表","link":"#常用命令列表","children":[]},{"level":2,"title":"安装软件","slug":"安装软件","link":"#安装软件","children":[]},{"level":2,"title":"公钥信任","slug":"公钥信任","link":"#公钥信任","children":[]},{"level":2,"title":"SSH登录","slug":"ssh登录","link":"#ssh登录","children":[]},{"level":2,"title":"系统信息","slug":"系统信息","link":"#系统信息","children":[]},{"level":2,"title":"系统操作","slug":"系统操作","link":"#系统操作","children":[]},{"level":2,"title":"用户相关","slug":"用户相关","link":"#用户相关","children":[]},{"level":2,"title":"文件相关","slug":"文件相关","link":"#文件相关","children":[]},{"level":2,"title":"程序相关","slug":"程序相关","link":"#程序相关","children":[]},{"level":2,"title":"网络相关","slug":"网络相关","link":"#网络相关","children":[]},{"level":2,"title":"其他","slug":"其他","link":"#其他","children":[]},{"level":2,"title":"系统工作命令","slug":"系统工作命令","link":"#系统工作命令","children":[]},{"level":2,"title":"系统状态检测命令","slug":"系统状态检测命令","link":"#系统状态检测命令","children":[]},{"level":2,"title":"查找定位文件命令","slug":"查找定位文件命令","link":"#查找定位文件命令","children":[]},{"level":2,"title":"环境变量","slug":"环境变量","link":"#环境变量","children":[]},{"level":2,"title":"搜索文件","slug":"搜索文件","link":"#搜索文件","children":[]},{"level":2,"title":"场景应用","slug":"场景应用","link":"#场景应用","children":[]},{"level":2,"title":"参考资料","slug":"参考资料","link":"#参考资料","children":[]}],"relativePath":"Linux/常用命令.md"}'),e={name:"Linux/常用命令.md"},p=n(`<h1 id="常用命令" tabindex="-1">常用命令 <a class="header-anchor" href="#常用命令" aria-hidden="true">#</a></h1><p>命令的具体用法可直接在手册中（<a href="https://www.linuxcool.com/" target="_blank" rel="noreferrer">https://www.linuxcool.com/</a> ）查询</p><h2 id="常用命令列表" tabindex="-1">常用命令列表 <a class="header-anchor" href="#常用命令列表" aria-hidden="true">#</a></h2><p>ps：查看进程</p><ul><li><code>ps -ef </code> 是否有进程</li><li><code>ps -ef | grep xxx</code>：管道筛选相应进程</li></ul><p>grep：管道</p><ul><li><code>ps -ef | grep redis</code>：查看启动的 redis 进程</li><li><code>ps -ef | grep nginx</code>：查看启动的 nginx 进程</li><li><code>ps -ef | grep node</code>：查看启动的 node 进程</li></ul><p>pwd：查看所在目录位置</p><p>ifconfig：查看网络设备信息</p><p>free：检查内存</p><ul><li><code>free -h</code>：查看内存，显示单位</li></ul><p><code>df -lh</code>：查看磁盘空间</p><p><code>mkdir</code>：新建目录</p><p>du：查看占用空间</p><ul><li><code>du -sh *</code>：查看目录下的各个文件所占内存</li></ul><p><code>uname</code>：查看是什么系统</p><ul><li><code>uname -a</code>：查看系统全部信息</li><li><code>uname -r</code>：查看系统版本</li></ul><p>yum：安装</p><p>which：查看安装位置</p><p>wget：下载安装包</p><p>tar：解压</p><ul><li><code>tar -zxvf xxxx.tar.gz</code>：解压文件</li></ul><p>help：帮助手册</p><p>find：查找</p><ul><li><code>find -name &#39;*.jar&#39;</code></li><li><code>find / -name redis.conf</code></li></ul><p>cp：复制</p><p>mv：改名</p><ul><li><code>mv code-react-app app</code></li></ul><p><code>nohup</code>：后台启动程序</p><ul><li><code>nohup xxx &amp;</code>：后台启动程序</li></ul><p>jobs：查看后台程序</p><p>netstat：</p><ul><li><code>netstat -ntlp</code>：查看占用的端口</li></ul><p>tail：</p><ul><li><code>tail -n 10</code>：查看最新的10行日志</li></ul><p>sz：下载</p><ul><li><code>sz error.log</code>：下载日志到本地</li></ul><p>kill：杀掉进程</p><ul><li><code>kill -9 xx(进程号)</code></li><li>通过 <code>ps -ef</code> 来查找进程</li></ul><p>chmod：提高文件权限</p><ul><li><code>chmod 600 start.sh</code></li></ul><h2 id="安装软件" tabindex="-1">安装软件 <a class="header-anchor" href="#安装软件" aria-hidden="true">#</a></h2><p>yum(Ubuntu apt)安装软件</p><p>apt-get：centos</p><h2 id="公钥信任" tabindex="-1">公钥信任 <a class="header-anchor" href="#公钥信任" aria-hidden="true">#</a></h2><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#676E95;font-style:italic;"># 生成公钥</span></span>
<span class="line"><span style="color:#FFCB6B;">ssh-keygen</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-t</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">rsa</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-P</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&#39;&#39;</span></span>
<span class="line"></span>
<span class="line"><span style="color:#89DDFF;">[</span><span style="color:#A6ACCD;">johan@iZuf61fxudjflq5eqyrnwpZ </span><span style="color:#89DDFF;">~]</span><span style="color:#A6ACCD;">:</span><span style="color:#89DDFF;">~</span><span style="color:#A6ACCD;">$ ssh-keygen</span></span>
<span class="line"><span style="color:#82AAFF;">...</span></span>
<span class="line"><span style="color:#FFCB6B;">The</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">key</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">fingerprint</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">is:</span></span>
<span class="line"><span style="color:#FFCB6B;">SHA256:IeFPfrcQ3hhP64SRTAFzGIHl2ROcopl5HotRi2XNOGk</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">[</span><span style="color:#A6ACCD;">johan@iZuf61fxudjflq5eqyrnwpZ </span><span style="color:#89DDFF;">~]</span></span>
<span class="line"><span style="color:#FFCB6B;">The</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">key</span><span style="color:#89DDFF;">&#39;</span><span style="color:#C3E88D;">s randomart image is:</span></span>
<span class="line"><span style="color:#C3E88D;">+---[RSA 2048]----+</span></span>
<span class="line"><span style="color:#C3E88D;">|      .o*@=o     |</span></span>
<span class="line"><span style="color:#C3E88D;">|     ..oEB=o     |</span></span>
<span class="line"><span style="color:#C3E88D;">|      o@=+O .    |</span></span>
<span class="line"><span style="color:#C3E88D;">|      B=+o @ .   |</span></span>
<span class="line"><span style="color:#C3E88D;">|       =So* *    |</span></span>
<span class="line"><span style="color:#C3E88D;">|      . o. = .   |</span></span>
<span class="line"><span style="color:#C3E88D;">|            o    |</span></span>
<span class="line"><span style="color:#C3E88D;">|                 |</span></span>
<span class="line"><span style="color:#C3E88D;">|                 |</span></span>
<span class="line"><span style="color:#C3E88D;">+----[SHA256]-----+</span></span>
<span class="line"></span>
<span class="line"><span style="color:#C3E88D;"># 查看公钥</span></span>
<span class="line"><span style="color:#C3E88D;">cat .ssh/id_rsa.pub </span></span>
<span class="line"></span>
<span class="line"><span style="color:#C3E88D;"># 将公钥拷贝到服务器</span></span>
<span class="line"><span style="color:#C3E88D;">scp ~/.ssh/id_rsa.pub root@47.102.152.XXX:/root</span></span>
<span class="line"></span>
<span class="line"><span style="color:#C3E88D;"># 将公钥加入信任列表</span></span>
<span class="line"><span style="color:#C3E88D;">cat id_dsa.pub &gt;&gt; ~/.ssh/authorized_keys</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br><span class="line-number">9</span><br><span class="line-number">10</span><br><span class="line-number">11</span><br><span class="line-number">12</span><br><span class="line-number">13</span><br><span class="line-number">14</span><br><span class="line-number">15</span><br><span class="line-number">16</span><br><span class="line-number">17</span><br><span class="line-number">18</span><br><span class="line-number">19</span><br><span class="line-number">20</span><br><span class="line-number">21</span><br><span class="line-number">22</span><br><span class="line-number">23</span><br><span class="line-number">24</span><br><span class="line-number">25</span><br><span class="line-number">26</span><br><span class="line-number">27</span><br><span class="line-number">28</span><br></div></div><h2 id="ssh登录" tabindex="-1">SSH登录 <a class="header-anchor" href="#ssh登录" aria-hidden="true">#</a></h2><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#676E95;font-style:italic;"># root 为用户名</span></span>
<span class="line"><span style="color:#676E95;font-style:italic;"># ip地址</span></span>
<span class="line"><span style="color:#FFCB6B;">ssh</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">root@47.102.152.XXX</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br></div></div><h2 id="系统信息" tabindex="-1">系统信息 <a class="header-anchor" href="#系统信息" aria-hidden="true">#</a></h2><ul><li>uname：查看系统细心</li><li>hostname：查看主机名</li><li>cat /proc/cpuinfo：查看 CPU 信息</li><li>lsmod：查看已加载的系统模块</li><li>top：查看系统使用情况</li><li>df：查看系统使用情况</li><li>fdisk：查看磁盘分区</li><li>du：查看目录使用情况</li><li>iostat：查看 I/O 使用情况</li><li>env：查看环境变量</li><li>uptime：查看系统运行时间、用户数、负载</li></ul><h2 id="系统操作" tabindex="-1">系统操作 <a class="header-anchor" href="#系统操作" aria-hidden="true">#</a></h2><ul><li>shutdown：关机</li><li>reboot：重启</li><li>mount：挂载设备</li><li>umount：卸载设备</li></ul><h2 id="用户相关" tabindex="-1">用户相关 <a class="header-anchor" href="#用户相关" aria-hidden="true">#</a></h2><ul><li>su：切换用户</li><li>sudo：以管理员身份执行</li><li>who：查看当前用户名</li><li>ssh：远程连接</li><li>logout：注销</li><li>useradd：创建用户</li><li>userdel：删除用户</li><li>usermod：修改用户</li><li>groupadd：创建用户组</li><li>groupdel：删除用户组</li><li>groupmod：修改用户组</li><li>passwd：修改密码</li><li>last：显示用户或终端的登录情况</li></ul><h2 id="文件相关" tabindex="-1">文件相关 <a class="header-anchor" href="#文件相关" aria-hidden="true">#</a></h2><ul><li>cd：切换目录</li><li>ls：查看目录列表</li><li>tree：打印目录数</li><li>mkdir：创建目录</li><li>rm：删除目录</li><li>touch：新建文件</li><li>cp：复制文件</li><li>mv：移动文件</li><li>ln：创建文件链接</li><li>find：搜索文件</li><li>locate：定位文件</li><li>whereis：查看可执行文件路径</li><li>which：在 PATH 指定的路径中，搜索某系统命令的位置</li><li>chmod：设置目录权限</li><li>cat/more/less：查看文件</li><li>tac：倒叙查看文件</li><li>head/tail：查看文件开头/ 结尾</li><li>paste：合并文件</li><li>zip/tar/gzip：压缩文件</li><li>unzip/tar/gunzip：解压文件</li><li>grep/sed/awd：文本处理</li><li>vim：文本编辑</li></ul><h2 id="程序相关" tabindex="-1">程序相关 <a class="header-anchor" href="#程序相关" aria-hidden="true">#</a></h2><ul><li>cronab：计划任务</li><li>nohup：后台运行程序</li><li>jobs：查看i系统任务</li><li>ps：查看进程</li><li>kill：杀死进程</li><li>rpm/yum/apt/apt-get/dpkg：软件包管理</li><li>service/systemctl：服务管理</li></ul><h2 id="网络相关" tabindex="-1">网络相关 <a class="header-anchor" href="#网络相关" aria-hidden="true">#</a></h2><ul><li><p>ifconfig：查看网络属性</p></li><li><p>netstat：查看网络状态</p><ul><li>常用命令：<code>netstat -ntlp</code></li></ul></li><li><p>iptables：查看 iptables 规则</p></li></ul><h2 id="其他" tabindex="-1">其他 <a class="header-anchor" href="#其他" aria-hidden="true">#</a></h2><ul><li>date 显示系统时间</li><li>cal 显示日历</li><li>history 显示与操作历史</li><li>help 帮助</li><li>alias 别名</li></ul><h2 id="系统工作命令" tabindex="-1">系统工作命令 <a class="header-anchor" href="#系统工作命令" aria-hidden="true">#</a></h2><p><strong>echo命令</strong></p><p>echo命令用于在终端设备上输出字符串或变量提取后的值，语法格式为「echo [字符串] [$变量]」</p><p><strong>ps命令</strong></p><p>ps命令用于查看系统中的进程状态，英文全称为“processes”，语法格式为「ps [参数]」</p><ul><li><code>ps -ef | gref nginx</code></li></ul><h2 id="系统状态检测命令" tabindex="-1">系统状态检测命令 <a class="header-anchor" href="#系统状态检测命令" aria-hidden="true">#</a></h2><p><strong>ifconfig</strong></p><p>ifconfig命令用于获取网卡配置与网络状态等信息，英文全称为“interface config”，语法格式为「ifconfig [参数] [网络设备]」</p><p><strong>uname</strong></p><p>uname命令用于查看系统内核版本与系统架构等信息，英文全称为“unix name”，语法格式为「uname [-a]」</p><p><strong>uptime</strong></p><p>uptime命令用于查看系统的负载信息，输入该命令后按回车键执行即可</p><p><strong>free</strong></p><p>free命令用于显示当前系统中内存的使用量信息，语法格式为「free [-h]」</p><p><strong>who</strong></p><p>who命令用于查看当前登入主机的用户终端信息，输入该命令后按回车键执行即可</p><p><strong>last</strong></p><p>last命令用于调取主机的被访记录，输入该命令后按回车键执行即可</p><p><strong>ping</strong></p><p>ping命令用于测试主机之间的网络连通性，语法格式为「ping [参数] 主机地址」</p><p><strong>tracepath</strong></p><p>tracepath命令用于显示数据包到达目的主机时途中经过的所有路由信息，语法格式为「tracepath [参数] 域名」</p><p><strong>netstat</strong></p><p>netstat命令用于显示如网络连接、路由表、接口状态等的网络相关信息，英文全称为“network status”，语法格式为「netstat [参数]」</p><p><strong>history</strong></p><p>history命令用于显示执行过的命令历史，语法格式为「history [-c]」</p><h2 id="查找定位文件命令" tabindex="-1">查找定位文件命令 <a class="header-anchor" href="#查找定位文件命令" aria-hidden="true">#</a></h2><p><strong>grep命令</strong></p><p>grep命令用于按行提取文本内容，语法格式为「grep [参数] 文件名称」</p><h2 id="环境变量" tabindex="-1">环境变量 <a class="header-anchor" href="#环境变量" aria-hidden="true">#</a></h2><table><thead><tr><th>命令</th><th>说明</th></tr></thead><tbody><tr><td>set</td><td>显示当前 bash 所有变量，包括其内奸环境，用户自定义变量及到处的环境变量</td></tr><tr><td>env</td><td>显示与当前用户相关的环境变量，还可以让命令在指定环境中运行</td></tr><tr><td>export</td><td>显示从 bash 中导出成环境变量的变量，也能通过它将自定义变量导出为环境变量</td></tr></tbody></table><p>临时生效：使用 export 命令行声明即可，变量在关闭 bash 时失效</p><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#C792EA;">export</span><span style="color:#A6ACCD;"> NODE_ENV</span><span style="color:#89DDFF;">=</span><span style="color:#C3E88D;">production</span></span>
<span class="line"><span style="color:#82AAFF;">echo</span><span style="color:#A6ACCD;"> $NODE_ENV</span></span>
<span class="line"><span style="color:#676E95;font-style:italic;"># production</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br></div></div><p>立即让他生效</p><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#82AAFF;">source</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">.zshrc</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><p>永久生效</p><p>这里介绍两个重要文件 <code>/etc/bashrc</code>（有的 Linux 没有这个文件） 和 <code>/etc/profile</code> ，它们分别存放的是 bash 变量和环境变量。还有要注意区别的是每个用户目录下的一个隐藏文件</p><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#FFCB6B;">ls</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-a</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><h2 id="搜索文件" tabindex="-1">搜索文件 <a class="header-anchor" href="#搜索文件" aria-hidden="true">#</a></h2><p>whereis，which，find，locate</p><p><code>whereis</code> 只能搜索二进制文件（<code>-b</code>），man 帮助文件（<code>-m</code>）和源代码文件（<code>-s</code>）。如果想要获得更全面的搜索结果可以使用 <code>locate</code> 命令</p><ul><li><p>locate 快而全</p></li><li><p><code>which</code> 小而精</p></li></ul><p><code>which</code> 本身是 bash 内建的一个命令，我们通常使用 <code>which</code> 来确定是否安装了某个指定的程序，因为它只从 <code>PATH</code> 环境变量指定的路径中去搜索命令并且返回第一个搜索到的结果。也就是说，我们可以看到某个系统命令是否存在以及执行的到底是哪一个地方的命令。</p><ul><li><code>find</code> 精而细</li></ul><p><code>find</code> 应该是这几个命令中最强大的了，它不但可以通过文件类型、文件名进行查找而且可以根据文件的属性（如文件的时间戳，文件的权限等）进行搜索。<code>find</code> 命令强大到，要把它讲明白至少需要单独好几节课程才行，我们这里只介绍一些常用的内容。</p><p>《黑客帝国》电影里满屏幕代码的“数字雨”，在 Linux 里面你也可以轻松实现这样的效果，你只需要一个命令 <code>cmatrix</code></p><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#FFCB6B;">sudo</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">apt-get</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">update</span></span>
<span class="line"><span style="color:#FFCB6B;">sudo</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">apt-get</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">install</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">cmatrix</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br></div></div><h2 id="场景应用" tabindex="-1">场景应用 <a class="header-anchor" href="#场景应用" aria-hidden="true">#</a></h2><p>后台开启任务</p><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#FFCB6B;">nohup</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">xxx</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&amp;</span></span>
<span class="line"><span style="color:#676E95;font-style:italic;"># 例如 nohup xeyes &amp;</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br></div></div><p>杀掉进程</p><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#FFCB6B;">sudo</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">kill</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-9</span><span style="color:#A6ACCD;"> </span><span style="color:#F78C6C;">501</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><h2 id="参考资料" tabindex="-1">参考资料 <a class="header-anchor" href="#参考资料" aria-hidden="true">#</a></h2><ul><li><a href="https://zhuanlan.zhihu.com/p/28674639" target="_blank" rel="noreferrer">写代码怎能不会这些 Linux 命令</a></li><li><a href="https://www.thegeekstuff.com/2010/11/50-linux-commands/" target="_blank" rel="noreferrer">50-linux-commands</a></li><li><a href="https://zhuanlan.zhihu.com/p/22654634" target="_blank" rel="noreferrer">linux 学习资源整理</a></li><li><a href="https://juejin.cn/post/6844903971815948296" target="_blank" rel="noreferrer">Linux 基础命令总结</a></li><li><a href="https://github.com/liyupi/code-roadmap/blob/main/docs/roadmap/Linux%E5%AD%A6%E4%B9%A0%E8%B7%AF%E7%BA%BF.md" target="_blank" rel="noreferrer">Linux学习路线</a></li></ul>`,117),i=[p];function r(o,t,c,d,h,u){return l(),a("div",null,i)}const y=s(e,[["render",r]]);export{m as __pageData,y as default};
